tsApp(                            //       text: "${text} \n \n"                            //           " لعنوان : "                            //           "   ${typed_location.value}\n"                            //           " رقم الهاتف  : "                            //           "   ${typed_Phone_number.value}\n");                            // });                          }                          Future.delayed(Duration(seconds: 2), () {                            flags.value = true;                          });                        },                        fillColor: Colorsapp.themeColor,                        shape: RoundedRectangleBorder(                            borderRadius: BorderRadius.circular(10)),                        child: flags.value == true                            ? Padding(                                padding:                                    const EdgeInsets.symmetric(horizontal: 50),                                child: Row(                                  mainAxisAlignment:                                      MainAxisAlignment.spaceBetween,                                  children: [                                    Icon(                                      Icons.whatsapp,                                      color: Colors.white,                                      size: 30,                                    ),                                    Text(                                        textDirection: TextDirection.rtl,                                        "أرسال عبر Whatsapp",                                        style: Text_Style.getstyle(                                            fontsize: 16,                                            ColorText: Colors.white,                                            fontWeight: FontWeight.w700)),                                  ],                                ),                              )                            : Load